const MOTIVADORES = [
  "Conhecimento","Contribuição","Espiritualidade","Experiência",
  "Financeiro","Justiça","Liberdade","Propósito","Realização","Saúde"
];

function ordenacaoParaPontos(ord) { return ord.map(p => 11 - p); }

function acumularPontos(todas) {
  const t = new Array(10).fill(0);
  todas.forEach(pts => pts.forEach((v, i) => { t[i] += v; }));
  return t;
}

function normalizarEmPercentual(totais) {
  const soma = totais.reduce((a, b) => a + b, 0);
  return totais.map(v => parseFloat(((v / soma) * 100).toFixed(2)));
}

function gerarRanking(pcts) {
  return pcts
    .map((p, i) => ({ motivador: MOTIVADORES[i], percentual: p, indice: i }))
    .sort((a, b) => b.percentual - a.percentual)
    .map((item, pos) => ({ ...item, posicao: pos + 1 }));
}

function calcularMotivadorDominante(segmentos, finais) {
  const todas = [];
  segmentos.forEach(o => todas.push(ordenacaoParaPontos(o)));
  finais.forEach(o => todas.push(ordenacaoParaPontos(o)));
  const totais = acumularPontos(todas);
  const pcts = normalizarEmPercentual(totais);
  let ranking = gerarRanking(pcts);

  // Desempate pelas finais
  const lideres = ranking.filter(r => r.percentual === ranking[0].percentual);
  if (lideres.length > 1) {
    const totaisFinais = acumularPontos(finais.map(o => ordenacaoParaPontos(o)));
    lideres.sort((a, b) => totaisFinais[b.indice] - totaisFinais[a.indice]);
    const restantes = ranking.filter(r => r.percentual !== ranking[0].percentual);
    ranking = [
      ...lideres.map((l, i) => ({ ...l, posicao: i + 1 })),
      ...restantes.map((r, i) => ({ ...r, posicao: lideres.length + i + 1 }))
    ];
  }
  return { motivadorDominante: ranking[0].motivador, ranking };
}

