// COMPONENTE DRAG-AND-DROP
// ─────────────────────────────────────────
function DragList({ items, onChange }) {
  const dragItem = useRef(null);
  const dragOver = useRef(null);

  const handleDragStart = (i) => { dragItem.current = i; };
  const handleDragEnter = (i) => { dragOver.current = i; };
  const handleDragEnd = () => {
    const newItems = [...items];
    const dragged = newItems.splice(dragItem.current, 1)[0];
    newItems.splice(dragOver.current, 0, dragged);
    dragItem.current = null;
    dragOver.current = null;
    onChange(newItems);
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", gap: 8 }}>
      {items.map((item, i) => (
        <div
          key={item.id}
          draggable
          onDragStart={() => handleDragStart(i)}
          onDragEnter={() => handleDragEnter(i)}
          onDragEnd={handleDragEnd}
          onDragOver={e => e.preventDefault()}
          style={{
            display: "flex", alignItems: "center", gap: 12,
            background: "rgba(136,104,172,0.08)",
            border: "1px solid rgba(136,104,172,0.25)",
            borderRadius: 10, padding: "12px 16px",
            cursor: "grab", userSelect: "none",
            transition: "all 0.15s ease"
          }}
        >
          <span style={{
            minWidth: 28, height: 28, borderRadius: "50%",
            background: "linear-gradient(135deg,#8868ac,#8f89ee)",
            display: "flex", alignItems: "center", justifyContent: "center",
            color: "#fff", fontWeight: 700, fontSize: 13, flexShrink: 0
          }}>{i + 1}</span>
          <span style={{ color: "#f6efe8", fontSize: 14, lineHeight: 1.5 }}>{item.texto}</span>
          <span style={{ marginLeft: "auto", color: "#8868ac", fontSize: 18, flexShrink: 0 }}>⠿</span>
        </div>
      ))}
    </div>
  );
}

