async function carregarJsPDF() {
  if (window.jspdf) return window.jspdf.jsPDF;
  return new Promise((resolve, reject) => {
    const script = document.createElement("script");
    script.src = "https://cdnjs.cloudflare.com/ajax/libs/jspdf/2.5.1/jspdf.umd.min.js";
    script.onload = () => resolve(window.jspdf.jsPDF);
    script.onerror = reject;
    document.head.appendChild(script);
  });
}

// Cores do template
const C = {
  creme:   [246, 239, 232],
  branco:  [255, 255, 255],
  preto:   [0, 0, 0],
  vinho:   [112, 20, 12],
  ameixa:  [127, 69, 106],
  roxoM:   [130, 81, 129],
  roxoC:   [133, 92, 152],
  lilas:   [136, 104, 172],
  roxoS:   [142, 126, 217],
  peri:    [143, 137, 238],
};

const W = 280, H = 157.5; // A4 landscape 16:9 em mm

function pdfRect(doc, x, y, w, h, color) {
  doc.setFillColor(...color);
  doc.rect(x, y, w, h, "F");
}

function pdfText(doc, text, x, y, size, color, opts = {}) {
  doc.setFontSize(size);
  doc.setTextColor(...color);
  if (opts.bold) doc.setFont("helvetica", "bold");
  else doc.setFont("helvetica", "normal");
  if (opts.center) doc.text(text, x, y, { align: "center" });
  else if (opts.right) doc.text(text, x, y, { align: "right" });
  else doc.text(text, x, y);
}

function wrapText(doc, text, x, y, maxW, size, color, lineH, bold = false) {
  doc.setFontSize(size);
  doc.setTextColor(...color);
  doc.setFont("helvetica", bold ? "bold" : "normal");
  const lines = doc.splitTextToSize(text, maxW);
  lines.forEach((line, i) => {
    doc.text(line, x, y + i * lineH);
  });
  return y + lines.length * lineH;
}

function drawSidebar(doc, label = "DIAGNÓSTICO") {
  pdfRect(doc, 0, 0, 22, H, C.vinho);
  // "I" no topo
  pdfText(doc, "I", 11, 16, 22, C.branco, { bold: true, center: true });
  // texto vertical
  doc.setFontSize(10);
  doc.setFont("helvetica", "bold");
  doc.setTextColor(...C.branco);
  doc.text(label, 11, H / 2 + 18, { angle: 90, align: "center" });
}

function drawFooter(doc) {
  pdfText(doc, "CLUB HY(PER)²", W - 6, H - 3, 6, C.lilas, { bold: true, right: true });
}

